package io.slychat.messenger.android.activites.services.impl

import android.support.v7.app.AppCompatActivity
import com.google.i18n.phonenumbers.NumberParseException
import com.google.i18n.phonenumbers.PhoneNumberUtil
import com.google.i18n.phonenumbers.Phonenumber
import io.slychat.messenger.android.AndroidApp
import io.slychat.messenger.android.activites.services.AndroidContactService
import io.slychat.messenger.core.UserId
import io.slychat.messenger.core.persistence.AllowedMessageLevel
import io.slychat.messenger.core.persistence.ContactInfo
import io.slychat.messenger.services.contacts.ContactEvent
import io.slychat.messenger.services.getAccountRegionCode
import nl.komponents.kovenant.Promise
import nl.komponents.kovenant.functional.bind
import nl.komponents.kovenant.functional.map
import nl.komponents.kovenant.ui.successUi
import rx.Subscription
import java.util.regex.Pattern

class AndroidContactServiceImpl(private val activity: AppCompatActivity): AndroidContactService {
    private val app = AndroidApp.get(activity)
    private val contactService = app.getUserComponent().contactsService

    private var contactListener: Subscription? = null
    private var contactUIListener: ((ContactEvent) -> Unit)? = null

    var contactList: MutableMap<UserId, ContactInfo> = mutableMapOf()

    init {
        getContacts()
    }

    override fun addContactListener(listener: ((ContactEvent) -> Unit)) {
        contactUIListener = listener
        contactListener = contactService.contactEvents.subscribe {
            handleContactEvent(it)
        }
    }

    override fun getAll(): Promise<List<ContactInfo>, Exception> {
        return contactService.getAll()
    }

    override fun clearListeners() {
        contactUIListener = null
        contactListener?.unsubscribe()
    }

    override fun getContacts(): Promise<MutableMap<UserId, ContactInfo>, Exception> {
        return contactService.getAll() map { contacts ->
            contactList = mutableMapOf()
            contacts.forEach {
                contactList.put(it.id, it)
            }
            contactList
        }
    }

    override fun getContact(id: UserId): Promise<ContactInfo?, Exception> {
        if (contactList[id] != null)
            return Promise.ofSuccess(contactList[id])

        return contactService.get(id) map { contact ->
            if (contact != null) {
                contactList[contact.id] = contact
            }
            contact
        }
    }

    override fun getBlockedContacts(): Promise<List<ContactInfo>, Exception> {
        return contactService.getAll() map { contacts ->
            val contactList = mutableMapOf<UserId, ContactInfo>()
            contacts.forEach {
                contactList.put(it.id, it)
            }
            contactList
        } bind { contactList ->
            val blockedList = mutableListOf<ContactInfo>()
            contactService.getBlockList() map { blocked ->
                blocked.forEach {
                    val contact = contactList[it]
                    if (contact !== null)
                        blockedList.add(contact)
                }
                blockedList
            }
        }
    }

    override fun deleteContact(contactInfo: ContactInfo): Promise<Boolean, Exception> {
        return contactService.removeContact(contactInfo)
    }

    override fun blockContact(id: UserId): Promise<Unit, Exception> {
        return contactService.block(id)
    }

    override fun unblockContact(id: UserId): Promise<Unit, Exception> {
        return contactService.unblock(id)
    }

    override fun getContactCount(): Promise<Int, Exception> {
        return contactService.getAll() map {
            it.size
        }
    }

    override fun fetchNewContactInfo(username: String): Promise<ContactInfo?, Exception> {
        val email = parseEmail(username)

        val phone = parsePhoneNumber(username)

        return contactService.fetchRemoteContactInfo(email, phone) map { response ->
            response.contactInfo?.toCore(AllowedMessageLevel.ALL)
        }
    }

    override fun addContact(contactInfo: ContactInfo): Promise<Boolean, Exception> {
        return contactService.addByInfo(contactInfo)
    }

    override fun allowAll(userId: UserId): Promise<Unit, Exception> {
        return contactService.allowAll(userId)
    }

